 (
  fileContextualPromptResults?: ContextualPromptResult[] | null
) => {
  const router = useRouter();
  const [defaultVariant] = useStore("generativeWeb.defaultVariant");

  /**
   * Variant either comes from URL or from the stored default variant slug from localStorage
   */
  const variantResultPromptSlug = router.query?.variant
    ? takeFirst(router.query.variant)
    : defaultVariant
    ? defaultVariant
    : undefined;

  const variantResult = fileContextualPromptResults?.find(
    (x) => x.contextualPromptSlug === variantResultPromptSlug
  );

  return variantResult;
}
