 (props: {
  projectRelativeFilePath?: string;
  folderPath: string;
  isFolder?: boolean;
  filename?: string;
  contextualPromptResults?: ContextualPromptResult[];
}) => {
  const { contextualPromptResults, folderPath, isFolder, filename } = props;

  const router = useRouter();
  const [defaultVariant, setDefaultVariant] = useStore(
    "generativeWeb.defaultVariant"
  );
  const [isEditing, setIsEditing] = useStore("generativeWeb.isEditing");
  const admin = useAdmin();
  const variantResult = useVariantResult(contextualPromptResults);

  const resultItems =
    contextualPromptResults?.map((x) => {
      const item: Item<string> = {
        label: `${x.isFake ? "üß™" : ""}${x.isFavorite ? "‚≠êÔ∏è" : ""}${
          x.contextualPromptSlug
        }`,
        value: x.contextualPromptSlug,
      };

      return item;
    }) || [];

  const items: Item<string>[] = [
    { label: "Original", value: "___NONE___" },
    ...resultItems,
  ];

  const withValue = (value: Item<string> | null | undefined) => {
    const query: {
      queryPath: string | string[] | undefined;
      variant?: string;
    } = {
      queryPath: router.query.queryPath,
    };

    if (value && value.value && value.value !== "___NONE___") {
      query.variant = value.value;
    }
    if (value?.value === "___NONE___") {
      setDefaultVariant(null);
    }
    router.push({ query }, undefined, { shallow: true });
  };

  return (
    <Div
      className=" 
    lg:flex-row lg:w-full w-min"
    >
      <Div className="dark:bg-gray-700 dark:hover:bg-gray-600 rounded-md border border-black p-2 m-1 cursor-pointer flex flex-row">
        <P>Variant:</P>
        <Select
          className="bg-transparent"
          title="Test"
          onChange={withValue}
          value={items.find(
            (x) => x.value === variantResult?.contextualPromptSlug
          )}
          options={items}
        />
        {/* {variantResult?.id ? (
          <ALink
            href={`${folderPath}/.index/${
              isFolder ? "" : filename ? withoutExtension(filename) : ""
            }`}
          >
            Go to index
          </ALink>
        ) : null} */}

        {(variantResult?.contextualPromptSlug || null) !== defaultVariant ? (
          <ClickableIcon
            emoji="üìå"
            onClick={() =>
              setDefaultVariant(variantResult?.contextualPromptSlug || null)
            }
          />
        ) : null}

        {variantResult && admin.isAdminActive ? (
          // delete, star/unstar
          <Div className="flex flex-row">
            <ClickableIcon
              emoji="üóë"
              onClick={async () => {
                if (!variantResult.prompt_projectRelativePath) return;
                const apiResult = await api.deletePromptResult(
                  variantResult.prompt_projectRelativePath,
                  variantResult.id
                );
                showStandardResponse(apiResult);

                if (apiResult?.result?.isSuccessful) {
                  admin.refetch?.();
                }
              }}
            />
            <ClickableIcon
              emoji={variantResult.isFavorite ? "üí©" : "‚≠êÔ∏è"}
              onClick={async () => {
                if (!variantResult.prompt_projectRelativePath) return;
                const apiResult = await api.setIsFavoritePromptResult(
                  variantResult.prompt_projectRelativePath,
                  variantResult.id,
                  !variantResult.isFavorite
                );
                showStandardResponse(apiResult);

                if (apiResult?.result?.isSuccessful) {
                  admin.refetch?.();
                }
              }}
            />
          </Div>
        ) : null}
      </Div>
    </Div>
  );
}
