(props: {
  basePath?: string;
  navigation?: FolderContent[];
  projectRelativeFilePath?: string;
}) => {
  const { basePath, navigation, projectRelativeFilePath } = props;
  const actions = useFileActions(basePath, navigation);
  const admin = useAdmin();
  const alert = useAlert();
  const { save, isSaved } = useFileWriter({
    projectRelativeFilePath,
    hideButtons: true,
    initialWriterView: "edit",
  });

  const saveAction: ContextMenuItem = {
    getTitle: () => "ðŸ’¾ Save",
    getIsEnabled: () => !isSaved,
    onClick: () => save(),
  };

  const allActions = [saveAction].concat(actions);

  return (
    <Div className="flex flex-row flex-wrap">
      {allActions.map((action) => {
        if (action.getIsEnabled?.(projectRelativeFilePath) === false) {
          return null;
        }

        return (
          <Div
            className="hover:bg-black/10 p-2 cursor-pointer"
            onClick={async () => {
              await action.onClick(projectRelativeFilePath);

              if (!admin.refetch) {
                alert?.("WTF no admin");
              }
              admin.refetch?.();
            }}
          >
            {action.getTitle(projectRelativeFilePath)}
          </Div>
        );
      })}
    </Div>
  );
}
