value:
(props: {
  selectionContextualPrompts?: ContextualPrompt[];
  contentString: string;
  projectRelativeFilePath?: string;
}) => {
  const showPromptAlert = usePromptResultAlert();
  const alert = useAlert();
  const { selectionContextualPrompts, contentString, projectRelativeFilePath } =
    props;
  const admin = useAdmin();

  const { selection: contextSelection, reset } = useLastSelection();

  /**
   * TODO: also sort them here based on which selection you make (`.contextualContent.contextSelection`) and your settings (probably isFavorite first, but other sorting may be beneficial.
   */
  const promptItems: SelectionItem[] =
    selectionContextualPrompts?.map((prompt) => {
      const item: SelectionItem = {
        onClick: () =>
          processPrompt({
            contextualPromptSlug: prompt.slug,
            contextualContent: {
              contextSelection,
              contextContent: contentString,
              context_projectRelativeFilePath: projectRelativeFilePath,
            },
            showPromptAlert,
          }),
        title: prompt.name || "Noname",
        // TODO: not sure if this is the right level, but eventually we want to filter based on the selection, maybe
        isEnabled: true,
      };

      return item;
    }) || [];

  const selectionWordCount = contextSelection
    ? contextSelection.split(" ").length
    : 0;

  const isStatement = selectionWordCount > 1;
  const isWord = selectionWordCount <= 4;

  const selectionItems: SelectionItem[] = [
    {
      onClick: async () => {
        if (!contextSelection) {
          alert?.(
            "Select a word",
            `You didn't select a word or word-combination (up to 4 words)`
          );
          return;
        }
        const apiResult = await api.addWord(contextSelection, undefined);

        showStandardResponse(apiResult);

        admin.refetch?.();
      },
      title: "ðŸ§  Add word",
      isEnabled: isWord,
    },

    {
      onClick: async () => {
        if (!contextSelection) {
          alert?.(
            "Select a statement",
            `You didn't select a statement (minimum 2 words)`
          );
          return;
        }

        // NB: for now, don't let user add agreement and importancy, just assume the defaults. Let's see where this goes first

        const apiResult = await api.addStatement(
          contextSelection,
          undefined,
          undefined
        );

        showStandardResponse(apiResult);

        admin.refetch?.();
      },
      title: "ðŸ§  Add statement",
      isEnabled: isStatement,
    },

    {
      title: "ðŸ•Š Free-form prompt",
      isEnabled: true,
      onClick: async () => {
        let question = prompt("What do you want to ask/prompt?");

        if (!question || question === "") {
          return;
        }

        let name = prompt(
          "How should we call the prompt? (empty for not storing)"
        );
        const realName = name === "" ? null : name;

        const customPromptContent = `I am reading this file:\n\n\`\`\`\n%context\n\`\`\`\n\nMore specifically, I'm looking at this part: \n\n\`\`\`\n%selection\n\`\`\`\n\n${question}`;

        await processPrompt({
          contextualContent: {
            contextContent: contentString,
            context_projectRelativeFilePath: projectRelativeFilePath,
            contextSelection,
          },
          showPromptAlert,
          customPromptContent,
          saveNewPromptWithName: realName,
        });
      },
    },

    {
      title: "ðŸŽ¤ Speak",
      isEnabled: true,
      onClick: () => {
        alert?.(
          "Coming soon!",
          <Div>
            <P>
              This button should let you record your voice so you can ask a
              question through human speech. The speech will be converted into
              text by whisper, it will then choose an existing prompt action, or
              if it can't find that, do a free-form prompt.
            </P>

            <P>
              P.S. Would be nice if this option is there by default, also for
              other menus, or it's even accessible via other ways, but this is a
              great way to try it out first.
            </P>
          </Div>
        );
      },
    },
  ];

  return (
    <Div>
      <P className="font-bold">Apply a prompt on your selection</P>
      <Div className="bg-black/20 dark:bg-white/20 flex flex-row justify-between rounded-md p-2 my-2">
        {contextSelection ? (
          <>
            <P>{contextSelection}</P>
            <ClickableIcon emoji="âŒ" onClick={() => reset()} />
          </>
        ) : (
          <P>Please select a piece of text first</P>
        )}
      </Div>

      {contextSelection ? (
        <Div className="flex flex-row flex-wrap">
          {selectionItems
            .filter((selectionItem) => selectionItem.isEnabled)
            .map((item, index) => {
              return (
                <NavButton
                  title={item.title}
                  onClick={item.onClick}
                  key={`sele${index}`}
                />
              );
            })}

          {selectionContextualPrompts?.map((prompt) => {
            return (
              <PromptButton
                key={`selectPrompt${prompt.id}`}
                item={prompt}
                contextSelection={contextSelection}
                markdown={contentString}
                projectRelativeFilePath={projectRelativeFilePath}
              />
            );
          })}
        </Div>
      ) : null}
    </Div>
  );
}
