() => {
  const queryPath = useQueryPath();
  const { refetch, isLoading } = queries.useGetReaderPageProps(
    process.env.NEXT_PUBLIC_BASEPATH || "",
    queryPath,
    true
  );

  const meQuery = queries.useGetMeWithContext();

  const isAdmin = !!meQuery.data?.result?.groups?.find(
    (x: Group) => x.slug === "admin"
  );

  const [config] = useStore("generativeWeb.config");

  const isAdminActive = isAdmin && !config.disableAdmin;
  const result: {
    isAdminActive: boolean;
    isLoading?: boolean;
    refetch?: typeof refetch;
  } = isAdminActive
    ? {
        isAdminActive,
        isLoading,
        refetch,
      }
    : { isAdminActive };

  return result;
}
