value:
(
  basePath?: string,
  navigation?: FolderContent[]
) => {
  const admin = useAdmin();

  const updateFrontmatter = async (
    projectRelativePath: string | undefined,
    newFrontmatter: { [key: string]: string }
  ) => {
    if (!projectRelativePath) return;

    if (!basePath) return;

    const apiResult = await api.updateFrontmatter({
      projectRelativePath,
      frontmatter: newFrontmatter,
    });

    showStandardResponse(apiResult);

    if (apiResult.result?.isSuccessful) {
      admin.refetch?.();
    }
  };

  const alert = useAlert();

  const items: ContextMenuItem[] = [
    {
      getTitle: () => "ðŸ—‘ Delete",
      onClick: (projectRelativePath) => {
        if (!projectRelativePath) return;

        if (!basePath) return;

        const basePathRelative = makeRelative(projectRelativePath, basePath);
        alert?.("Are you sure?", `Do you want to delete ${basePathRelative}?`, [
          { style: "cancel", text: "Cancel" },
          {
            text: "Delete",
            style: "destructive",
            onPress: async () => {
              // delete also works, but trash seems better for now.
              const apiResult = await api.deleteFileOrFolder(
                projectRelativePath
              );

              showStandardResponse(apiResult);

              if (apiResult?.result?.isSuccessful) {
                admin.refetch?.();
              }
            },
          },
        ]);
      },
    },
    {
      getTitle: () => "ðŸ“£ Rename",
      onClick: async (projectRelativePath) => {
        if (!projectRelativePath) return;
        let newFilename = prompt(
          "What should the new name be?",
          getFileOrFolderName(projectRelativePath)
        );
        if (!newFilename) return;
        const apiResult = await api.renameFileOrFolder(
          projectRelativePath,
          newFilename
        );

        showStandardResponse(apiResult);
        if (apiResult?.result?.isSuccessful) {
          admin.refetch?.();
        }
      },
    },
    {
      getTitle: () => "â†©ï¸ Move",
      onClick: async (projectRelativePath) => {
        if (!projectRelativePath) return;

        if (!basePath) return;

        const basePathRelative = makeRelative(projectRelativePath, basePath);
        let newLocation = prompt(
          "Where should this file/folder be moved to?",
          getFolderJs(basePathRelative)
        );
        if (!newLocation) return;

        const apiResult = await api.movePath(
          projectRelativePath,
          basePath + "/" + newLocation
        );

        showStandardResponse(apiResult);
        if (apiResult?.result?.isSuccessful) {
          admin.refetch?.();
        }
      },
    },

    {
      getTitle: () => "ðŸ‘¯â€â™€ï¸ Make a copy",
      onClick: async (projectRelativePath) => {
        if (!projectRelativePath) return;

        const apiResult = await api.copyPath(projectRelativePath);

        showStandardResponse(apiResult);

        if (apiResult?.result?.isSuccessful) {
          admin.refetch?.();
        }
      },
    },

    {
      getTitle: () => "ðŸ”Ž Open in Explorer",
      onClick: async (projectRelativePath) => {
        if (!projectRelativePath) return;

        if (!basePath) return;

        const apiResult = await api.fileExplorerOpen(projectRelativePath);

        showStandardResponse(apiResult);
      },
    },

    {
      getTitle: () => "ðŸ’» Open in VSCode",
      onClick: async (projectRelativePath) => {
        if (!projectRelativePath) return;

        if (!basePath) return;

        const apiResult = await api.vscodeOpen({
          files: [{ projectRelativePath }],
        });

        showStandardResponse(apiResult);
      },
    },

    {
      getIsEnabled: (projectRelativePath) =>
        navigation?.find((x) => x.projectRelativePath === projectRelativePath)
          ?.authorizedGroup !== "",
      getTitle: () => "ðŸ•Š Make free",
      onClick: (path) => updateFrontmatter(path, { authorizedGroup: "" }),
    },
    {
      getIsEnabled: (projectRelativePath) =>
        navigation?.find((x) => x.projectRelativePath === projectRelativePath)
          ?.authorizedGroup !== "premium",

      getTitle: () => "ðŸ› Make indie-only",
      onClick: (path) =>
        updateFrontmatter(path, { authorizedGroup: "premium" }),
    },

    {
      getIsEnabled: (projectRelativePath) =>
        navigation?.find((x) => x.projectRelativePath === projectRelativePath)
          ?.authorizedGroup !== "enterprise",
      getTitle: () => "ðŸš€ Make startup-only",
      onClick: (path) =>
        updateFrontmatter(path, { authorizedGroup: "enterprise" }),
    },

    {
      getIsEnabled: (projectRelativePath) =>
        !navigation?.find((x) => x.projectRelativePath === projectRelativePath)
          ?.isPrivate,
      getTitle: () => "ðŸ”‘ Set private",
      onClick: (path) => updateFrontmatter(path, { isPrivate: "true" }),
    },
    {
      getIsEnabled: (projectRelativePath) =>
        navigation?.find((x) => x.projectRelativePath === projectRelativePath)
          ?.isPrivate === true,
      getTitle: () => "ðŸ”‘ Set public",
      onClick: (path) => updateFrontmatter(path, { isPrivate: "false" }),
    },
    {
      getIsEnabled: (projectRelativePath) =>
        navigation?.find((x) => x.projectRelativePath === projectRelativePath)
          ?.isSecret !== true,
      getTitle: () => "ðŸ”’ Set secret",
      onClick: (path) => updateFrontmatter(path, { isSecret: "true" }),
    },
    {
      getIsEnabled: (projectRelativePath) =>
        navigation?.find((x) => x.projectRelativePath === projectRelativePath)
          ?.isSecret === true,
      getTitle: () => "ðŸ”’ Set open",
      onClick: (path) => updateFrontmatter(path, { isSecret: "false" }),
    },
    {
      getIsEnabled: (projectRelativePath) =>
        navigation?.find((x) => x.projectRelativePath === projectRelativePath)
          ?.isDraft !== true,
      getTitle: () => "ðŸ§ª Set draft",
      onClick: (path) => updateFrontmatter(path, { isDraft: "true" }),
    },
    {
      getIsEnabled: (projectRelativePath) =>
        navigation?.find((x) => x.projectRelativePath === projectRelativePath)
          ?.isDraft === true,
      getTitle: () => "ðŸ§ª Set live",
      onClick: (path) => updateFrontmatter(path, { isDraft: "false" }),
    },
  ];

  return items;
}
