value:
(
  interfaces: Creation<TsInterface>[]
): { name: string; schema: JSONSchema7 }[] => {
  const possibleRefs = interfaces
    .map((tsInterface) => {
      return tsInterface.type.typeDefinition
        ? {
            name: tsInterface.name,
            schema: tsInterface.type.typeDefinition,
          }
        : null;
    })
    .filter(notEmpty);

  return possibleRefs;
}
