export const getSimpleJsonString = (json: Json): string | undefined => {
  if (
    [
      "string",
      "number",
      "bigint",
      "boolean",
      "symbol",
      "undefined",
      // "function", // I don't think function is that simple, is it?
    ].includes(typeof json) ||
    json === null
  ) {
    return String(json);
  }
};
