 async () => {
  const [customManualProjectRoot] = process.argv.slice(2);

  if (!customManualProjectRoot) return dev();

  if (customManualProjectRoot.startsWith(".")) {
    // relative path
    const realProjectRootPath = path.join(
      process.cwd(),
      customManualProjectRoot
    );

    return dev(realProjectRootPath);
  }

  // absolute path
  dev(customManualProjectRoot);
}
