export const exploreOperationFolders = async (
  config: BaseConfig
): Promise<string[]> => {
  const folders = (
    await findAllPackages({
      basePath: config?.basePath,
    })
  )
    .map((x) => x.path)
    .map((p) => getFolder(p))
    .filter((p) => {
      const alsoTsConfig = fs.existsSync(path.join(p, "tsconfig.json"));
      return alsoTsConfig;
    });

  return folders;
};
