 (props: {
  /**
   * If given, menu will be rendered
   */
  menu?: MenuProps;

  overwriteDefaultPages?: string[];
  customHeader?: React.ReactNode;
  custom404Page?: React.ReactNode;
  // how to build up the menu

  // typed as any needed because of react-bug
  nextPage?: any;
  pageProps?: any;
}) => {
  const publicBundleConfig = queries.useGetPublicBundleConfig().data?.result;
  const {
    nextPage,
    pageProps,
    menu,
    customHeader,
    custom404Page,
    overwriteDefaultPages,
  } = props;
  const router = useRouter();
  const path = router.asPath.substring(1).split("?")[0];

  const pages: { [key: string]: (props?: any) => JSX.Element } = {
    login: LoginForm,
    signup: SignupForm,
    "edit-profile": UpdateMeForm,
    profile: PublicProfile,
    "2fa": AuthenticationMethodsCrud,
  };

  const NextPage = nextPage;

  // seems unneeded

  // const hasQueryPath = !!menu?.queryPaths?.includes(path);
  // const hasWebPage = !!menu?.webPagesFlat?.find((x) => x.queryPath === path);

  // const hasWebPageInThisFolder = !!menu?.webPagesFlat?.find((x) =>
  //   x.queryPath.startsWith(path)
  // );

  // const shouldRenderCustom =
  //   hasQueryPath || hasWebPage || path === "" || hasWebPageInThisFolder;

  const DefaultPage = pages[path] ? pages[path] : undefined;

  const children =
    DefaultPage && !overwriteDefaultPages?.includes(path) ? (
      <DefaultPage />
    ) : (
      <NextPage {...pageProps} />
    );

  const header = (
    <Div>
      {customHeader !== undefined ? (
        customHeader
      ) : (
        <Header publicBundleConfig={publicBundleConfig} />
      )}
    </Div>
  );

  return (
    <LoginWrapper>
      <LayoutGrid header={header} menu={menu}>
        {children}
      </LayoutGrid>
    </LoginWrapper>
  );
}
