 (props: { publicBundleConfig?: PublicBundleConfig }) => {
  const { publicBundleConfig } = props;

  const [isMobileMenuEnabled, setIsMobileMenuEnabled] = useStore(
    "menu.isMobileMenuEnabled"
  );

  return (
    <Div
      className="dark:bg-gray-800"
      style={{
        justifyContent: "space-between",
        flex: 1,
        display: "flex",
        flexDirection: "row",
        alignItems: "center",
      }}
    >
      <Div className="flex flex-row items-center">
        <ALink
          href={
            typeof window !== "undefined"
              ? location.protocol + "//" + location.host
              : "#"
          }
          style={{ fontSize: 32 }}
        >
          {publicBundleConfig?.emoji}
        </ALink>

        <P className="font-bold pl-2">{publicBundleConfig?.name}</P>
      </Div>

      <Div className="flex flex-row items-center">
        {props.publicBundleConfig?.isGitRepoPublic &&
        props.publicBundleConfig.gitRepoUrl ? (
          <ALink
            className="pr-2"
            target="_blank"
            href={props.publicBundleConfig.gitRepoUrl}
          >
            GitHub
          </ALink>
        ) : null}

        <Div className="lg:hidden">
          <ClickableIcon
            emoji="ðŸ”"
            onClick={() => setIsMobileMenuEnabled(!isMobileMenuEnabled)}
          />
        </Div>

        <MeAuthenticationInfo />
      </Div>
    </Div>
  );
}
