(props: {
  /**
   * If given, menu will be rendered
   */
  menu?: MenuProps;

  customHeader?: React.ReactNode;
  // how to build up the menu

  // typed as any needed because of react-bug
  nextPage?: any;
  pageProps?: any;
}) => {
  const publicBundleConfig = queries.useGetPublicBundleConfig().data?.result;
  const { nextPage, pageProps, menu, customHeader } = props;
  const router = useRouter();
  const path = router.asPath.substring(1).split("?")[0];

  const pages: { [key: string]: (props?: any) => JSX.Element } = {
    login: LoginForm,
    signup: SignupForm,
    "edit-profile": UpdateMeForm,
    profile: PublicProfile,
    "2fa": AuthenticationMethodsCrud,
  };

  const NextPage = nextPage;

  const DefaultPage = pages[path];
  const children = pages[path] ? <DefaultPage /> : <NextPage {...pageProps} />;

  const header = (
    <Div>
      {customHeader !== undefined ? (
        customHeader
      ) : (
        <Header publicBundleConfig={publicBundleConfig} />
      )}
    </Div>
  );
  return (
    <LoginWrapper>
      <LayoutGrid header={header} menu={menu}>
        {children}
      </LayoutGrid>
    </LoginWrapper>
  );
}
