(props: {
  /**
   * If given, menu will be rendered
   */
  menu?: MenuProps;

  customHeader?: React.ReactNode;
  // how to build up the menu

  // typed as any needed because of react-bug
  nextPage?: any;
  pageProps?: any;
}) => {
  const publicBundleConfig = queries.useGetPublicBundleConfig().data?.result;
  const { nextPage, pageProps, menu, customHeader } = props;
  const router = useRouter();
  const path = router.asPath.substring(1);

  const pages: { [key: string]: (props?: any) => JSX.Element } = {
    login: LoginForm,
    signup: SignupForm,
    "edit-profile": UpdateMeForm,
    profile: () => <Div>"should show person's profile"</Div>,
  };

  /**
   * I run into this problem all the time
   *
   * https://stackoverflow.com/questions/71616561/css-tailwind-grid-height-100vh-not-working
   *
   * final solution came from here
   *
   * https://stackoverflow.com/questions/47094742/fullscreen-flex-layouts-necessity-of-intermediate-flex-boxes
   *
   * Unfortunately, I can't seem to hide stuff anymore for some reason on non-mobile. "lg:flex hidden" just doesn't work, just hides!
   *
   * My elegant solution was to just use visible/invisible and make the mobile layout hover over the invisible one in case of a small screen, it works!
   */

  const NextPage = nextPage;

  const DefaultPage = pages[path];
  const children = pages[path] ? <DefaultPage /> : <NextPage {...pageProps} />;

  const header = (
    <Div>
      {customHeader !== undefined ? (
        customHeader
      ) : (
        <Header publicBundleConfig={publicBundleConfig} />
      )}
    </Div>
  );
  return (
    <LoginWrapper>
      <LayoutGrid header={header} menu={menu}>
        {children}
      </LayoutGrid>
    </LoginWrapper>
  );
}
