() => {
  // ping every time the user returns to the screen. this way the api gets checked
  const pingResult = queries.usePing();
  const [isOffline, setIsOffline] = useState<boolean | undefined>(undefined);
  useEffect(() => {
    const intervalId = setInterval(() => {
      // every second, check the localstorage for this thing.

      getItem(API_NO_RESPONSE_TIME_KEY).then((value) => {
        if (value === undefined) {
          if (isOffline) {
            setIsOffline(false);
            return;
          }
        }

        const lastNoResponse = Number(value);
        console.log({ lastNoResponse });

        if (lastNoResponse === 0) {
          // last time we got a response...
          setIsOffline(false);
          return;
        }

        const timeAgo = Date.now() - lastNoResponse;

        if (timeAgo > timeBeforeNotOffline) {
          // long ago, let's wait for loading it again
          setIsOffline(undefined);
          return;
        }

        // last 10 seconds we tried but itseems to have an error during that time
        setIsOffline(true);
      });
    }, 1000);

    return () => clearInterval(intervalId);
  }, []);

  return (
    <Div className="mx-4">
      <ALink href="/settings">
        {isOffline === undefined ? "ğŸŸ " : isOffline === true ? "âŒ" : "âœ…"} API
        status:{" "}
        {isOffline === true
          ? "down"
          : isOffline === undefined
          ? "loading"
          : "up"}
      </ALink>
    </Div>
  );
}
