(absolutePath: string) => {
  const parsedPath = path.parse(absolutePath);
  const potentialLockFilePath = path.join(
    parsedPath.dir,
    `${parsedPath.name}.lock`
  );

  if (fs.existsSync(potentialLockFilePath)) {
    const lock = readJsonFileSync<LockInfo>(potentialLockFilePath);

    // there's a lockfile
    if (lock?.updatedAt && Date.now() - lock.updatedAt < maximumLockTime) {
      // status has recently enough been updated
      return true;
    }
  }
  return false;
}
