export const getChunkParagraphsRecursively = (
  chunk: MarkdownChunk
): string[] => {
  const childrenParagraphs =
    chunk.children?.map(getChunkParagraphsRecursively).flat() || [];
  const thisParagraph = chunk.content;
  const paragraphs = [thisParagraph, ...childrenParagraphs].filter(notEmpty);
  return paragraphs;
};
