export const tsInterfaceToMarkdownString = (
  tsInterface: TsInterface
): string => {
  const titleString = `# ${tsInterface.name}`;
  const storageMethodString = tsInterface.dbStorageMethod
    ? `${tsInterface.dbStorageMethod} model\n\n`
    : undefined;

  // const operationName = tsInterface.operationName
  //   ? `(from: \`${tsInterface.operationName}\`)`
  //   : undefined;

  // const interfaceText = `\`\`\`ts\n${tsInterface.rawText}\n\`\`\``;

  const interfaceText = simplifiedSchemaToMarkdownString(
    tsInterface.type.simplifiedSchema,
    undefined,
    true,
    2
  );

  const alineas: (string | undefined)[] = [
    titleString,
    // operationName,
    storageMethodString,
    tsInterface.description,
    interfaceText,
  ];

  return alineas.filter(notEmpty).join("\n\n");
};
