 async (
  absoluteFolderPath: string,
  /**
   * filename to include in the final MarkdownParse
   */
  fileName?: string
): Promise<{ markdownParse: MarkdownParse; outline: string }> => {
  const mdPaths = (
    await explore({ basePath: absoluteFolderPath, extension: "md" })
  ).map((x) => x.path);

  const markdownParses = (
    await Promise.all(mdPaths.map((mdPath) => readMarkdownFile(mdPath)))
  ).filter(notEmpty);

  const merged = mergeMarkdownParse(markdownParses, fileName);

  const docFileNames = markdownParses.map((x) => x.fileName).filter(notEmpty);

  const outline =
    docFileNames.length > 0
      ? `## Docs\n\n${docFileNames
          .map((fileName) => {
            return `- [${humanCase(fileName)}](#${fileName})`;
          })
          .join("\n")}\n\n`
      : "";

  return { markdownParse: merged, outline };
}
