 (
  markdownChunks: MarkdownChunk[] | undefined
): MarkdownChunk[] | undefined => {
  if (!markdownChunks) return;

  const newMarkdownChunks: MarkdownChunk[] = markdownChunks.map((chunk) => ({
    ...chunk,
    level: chunk.level + 1,
    children: upMarkdownChunkLevelRecursively(chunk.children),
  }));

  return newMarkdownChunks;
}
