export const putReadmeOnTop = <
  T extends {
    path: string;
    isFolder: boolean;
  }
>(
  items: T[]
) => {
  const itemsReadmesFirst = items.reduce((previous, item, index, array) => {
    if (!item.path.toLowerCase().endsWith("readme.md")) {
      return previous;
    }

    const folderIndex = array.findIndex(
      (x) => x.isFolder && x.path === getFolder(item.path)
    );

    if (folderIndex === -1) {
      return previous;
    }

    return putIndexAtIndex(previous, index, folderIndex + 1);
  }, items);

  return itemsReadmesFirst;
};
