value:
(props: {
  content: string;
  config: MarkdownParseRenderConfig;
}): JSX.Element => {
  const { config, content } = props;
  const { openContextMenuProps, renderContextMenu, isOpen } =
    useSelectionPromptsMenu(config.selectionPrompts || [], {
      contextContent: content,
      context_projectRelativeFilePath: config.projectRelativeMarkdownFilePath,
    });

  if (!content) return <Div>No content</Div>;

  const markdownRender = React.useMemo(() => {
    return <MarkdownContentRender content={content} config={config} />;
  }, [
    content,
    isOpen,
    isOpen ? { renderContextMenu, openContextMenuProps } : undefined,
  ]);

  if (config.disableSelectionContextMenu) {
    return markdownRender;
  }

  return (
    <div {...openContextMenuProps}>
      {renderContextMenu()}
      {markdownRender}
    </div>
  );
}
