value:
(props: { text: string }) => {
  const { text } = props;
  if (!text) return null;
  const [parameter, value] = text
    .slice(1)
    .split(":")
    .map((x) => x.trim());
  return (
    <Div className="rounded-md bg-purple-400 dark:bg-purple-800">
      {parameter ? <Div>{parameter}</Div> : null}
      {value ? <Div className="bg-white bg-opacity-50">{value}</Div> : null}
    </Div>
  );
}
