(markdownString: string): string[] => {
  const result = flattenMarkdownString(
    markdownString,
    (token) => token.type === "code"
  );

  const codesblocks: string[] = result
    .map((token) => {
      if (token.type !== "code") return;

      return token.text;
    })
    .filter(notEmpty);

  return codesblocks;
}
