value:
(
  token: marked.Token
): MarkdownEmbed[] => {
  if (token.type === "image") {
    return [
      {
        src: token.href,
        alt: token.title,
        type: getTypeFromUrlOrPath(token.href),
      },
    ];
  }

  if (token.type === "paragraph") {
    const result = token.tokens.map(findEmbedsFromTokenRecursively).flat();
    return result;
  }

  // probably need to add more here because the images can be nested elsewhere too

  return [];
}
