 (
  /**
   * paths coming from `router.query`
   *
   * supposed to be a path/url refering to a file/page that can be represented with slashes in between
   */
  paths: string | string[] | undefined
): string => {
  return typeof paths === "string"
    ? paths
    : Array.isArray(paths)
    ? paths.join("/")
    : "";
}
