 async (
  operationBasePath: string
) => {
  const operationMetaData = await getOperationMetaData(operationBasePath);

  if (!operationMetaData) {
    log(`Operation on ${operationBasePath} doesn't have a basepath`, {
      type: "error",
    });
    return;
  }

  const {
    operationIndex,
    operationFolderName,
    operationName,
    relativeOperationLocationPath,
  } = operationMetaData;

  if (!operationName || !relativeOperationLocationPath) {
    log(
      `Operation on ${operationBasePath} doesn't have a name or relativeOperationLocationPath`,
      { type: "error" }
    );
    return;
  }

  const classification = getOperationClassification(operationBasePath)!; //NB: we know it's there because operationBasePath must be valid
  const dependencies = await getDependenciesSummary(operationName);

  const generalOperationIndex: GeneralOperationIndex = {
    updatedAt: Date.now(),
    folderName: operationFolderName,
    packageName: operationName!,
    relativeOperationLocationPath,
    classification,
    name: operationName!,
    slug: operationName!,
    ...dependencies,
  };

  const categories = await categorizeFiles({
    basePath: operationBasePath,
    ignoreIndexFiles: true,
  });
  const size = await getFolderSummary(categories);

  const newOperationIndex: Creation<OperationIndex> = operationIndex
    ? {
        ...operationIndex,
        ...generalOperationIndex,
        size,
        operationName,
      }
    : {
        // new
        deletedAt: 0,
        createdFirstAt: Date.now(),
        createdAt: Date.now(),
        // calculated
        ...generalOperationIndex,
        size,

        // NB: give default values for the ones that we didn't calculate yet
        buildSucceeded: false,
        dependenciesBuildsFailed: false,
        indexErrors: [],
        indexImportExportError: "",
        indexInteracesErrors: [],
        lintProblems: [],
        id: generateId(),
        language: "en",
        operationName,
      };

  // @ts-ignore
  await db.upsert("OperationIndex", newOperationIndex, { operationName });
}
