 async <
  K extends keyof OperationIndex
>(
  /**
   * can be a path to any file in the operation
   */
  absolutePath: string,
  key: K,
  value: OperationIndex[K]
) => {
  const operationBasePath = findOperationBasePath(absolutePath);
  if (!operationBasePath) return;

  const operationIndexJsonPath = path.join(
    operationBasePath,
    databaseFolderName,
    "operation-index.json"
  );
  if (!fs.existsSync(operationIndexJsonPath)) {
    const indexFolder = path.parse(operationIndexJsonPath).dir;
    if (!fs.existsSync(indexFolder)) {
      await fs.mkdir(indexFolder, { recursive: true });
    }
    await fs.writeFile(operationIndexJsonPath, "{}", "utf8");
  }

  setJsonKey({
    jsonPath: operationIndexJsonPath,
    keyLocation: key,
    value: JSON.stringify(value),
  });
}
