 async (): Promise<AppOperation[]> => {
  const appOperations = (await db.get("Operation", { operationName: "*" }))
    .filter(
      (operation) =>
        operation.name && Object.keys(ports).includes(operation.name)
    )
    .map((operation) => {
      const operationConfig = operation.operation;

      const description = operationConfig?.markdown || operation.description;
      return {
        name: operation.name,
        port: ports[operation.name as keyof typeof ports],
        description,
        emoji: getFirstEmoji(description),
      };
    })
    .sort((a, b) => {
      if (a.port < b.port) return -1;
      return 1;
    });

  const appOperationsWithOnline: AppOperation[] = await Promise.all(
    appOperations.map(async (appOperation) => {
      const isOnline = await isPortUsed(appOperation.port);
      const newAppOperation: AppOperation = {
        ...appOperation,
        isOnline,
      };
      return newAppOperation;
    })
  );

  return appOperationsWithOnline;
}
