 async (
  baseFolderPath: string
): Promise<NestedPathObject> => {
  // 1 - explore

  const exploreResult = await explore({
    basePath: baseFolderPath,
    includeFoldersWithResults: true,
  });
  // 2 - get file and folder paths from explore result
  const explorePaths = exploreResult.map((textJson) => textJson.path);
  const relativeExplorePaths = explorePaths.map((explorePath) => {
    const baseFolderRelativeExplorePath = makeRelative(
      explorePath,
      baseFolderPath
    );

    return baseFolderRelativeExplorePath;
  });

  // 3- convert file/folder paths array into `NestedPathObject`
  const nestedPathObject =
    queryPathsArrayToNestedPathObject(relativeExplorePaths);

  // 4 - return that
  return nestedPathObject;
}
