 async (
  peerSlug: string
): Promise<
  | undefined
  | { peerApiResult: RealApiReturnType<"getPublicFolderNestedPathObject"> }
> => {
  const { peerPeople } = await getPeerPeople();
  const peer = peerPeople.find((x) => x.slug === peerSlug);
  const name = peer?.name;
  if (!name) {
    console.log("Peer not found");
    return;
  }

  // TODO:
  const ip = "";
  // call `getPublicFolderNestedPathObject` with api with other ip
  const peerApiResult = await apiWithConfig.getPublicFolderNestedPathObject({
    apiUrl: `http://${ip}:${ports["function-server"]}`,
    // authToken: peer.authToken,
    timeout: 5000,
  });

  return { peerApiResult };
}
