 async (
  message: string,
  peerSlug: string
): Promise<boolean> => {
  await addPeerMessage(message, peerSlug);

  const peers = await db.get("Person", {
    include: { referenceKey: "personId" },
  });
  await Promise.all(
    peers.map(async (peer) => {
      //  if (peer.) return;
      const apiResult = await apiWithConfig.addPeerMessage(
        {
          apiUrl: `http://${peer.name}:${ports["function-server"]}`,
          //  authToken: peer,
          timeout: 2000,
        },
        message,
        peerSlug
      );

      return apiResult;
    })
  );

  return true;
}
