value:
async (
  ip: string,
  authToken: string,
  peerName?: string,
  /**
   * If true, it does not validate the IP to see if it is online and it is authorized...
   */
  force?: boolean,
  isMe?: boolean
) => {
  // get new PeerMessages after last sync

  const apiResult = await apiWithConfig.ping({
    apiUrl: `http://${ip}:${ports["function-server"]}`,
    authToken,
    timeout: 2000,
  });

  const isConnectionEstablished =
    apiResult?.isSuccessful &&
    !apiResult?.isUnauthorized &&
    apiResult.result === true;

  if (!force && !isConnectionEstablished) {
    return {
      isSuccesful: false,
      message:
        "Peer connection could not be established. Are you sure the peer is online? Use force if you want to add your peer anyway. If you have a node version lower than v17, this will also fail.",
    };
  }
}
