async (device: Device) => {
  const { result: appOperationsCalculated } =
    await apiWithConfig.getAllAppOperations({
      apiUrl: `http://${device.ip}:${ports["function-server"]}`,
      authToken: device.authToken,
      timeout: 1000,
    });

  const lastOnlineAt = Date.now();
  if (!!appOperationsCalculated) {
    db.update(
      "Device",
      (p) => p.id === device.id,
      (p) => ({ ...p, lastOnlineAt })
    );
  }

  return { ...device, lastOnlineAt, appOperationsCalculated };
}
