async (): Promise<AppOperation[]> => {
  const appOperations = (await db.get("Operation"))
    .filter(
      (operation) =>
        operation.name &&
        operation.operation?.classificationIndexed === "ui-web" &&
        operation.operation?.port
    )
    .map((operation, index) => {
      const description =
        operation.operation?.markdown || operation.description;
      const port = operation.operation?.port!;

      return {
        name: operation.name,
        port,
        description,
        emoji: getFirstEmoji(description),
      };
    })
    .sort((a, b) => {
      if (!a.port || !b.port) return -1;

      if (a.port < b.port) return -1;
      return 1;
    });

  const appOperationsWithOnline: AppOperation[] = await Promise.all(
    appOperations.map(async (appOperation) => {
      const isOnline = await isPortUsed(appOperation.port);
      const newAppOperation: AppOperation = {
        ...appOperation,
        isOnline,
      };
      return newAppOperation;
    })
  );

  return appOperationsWithOnline;
}
