async (): Promise<AppOperation[]> => {
  const operationConfigs = await db.get("OperationConfig");
  const appOperations = (await db.get("PackageJson"))
    .filter(
      (packageJson) =>
        packageJson.name && Object.keys(ports).includes(packageJson.name)
    )
    .map((packageJson) => {
      const operationConfig = operationConfigs.find(
        (x) => x.operationName === packageJson.operationName
      );
      const description = operationConfig?.markdown || packageJson.description;
      return {
        name: packageJson.name!,
        port: ports[packageJson.name as keyof typeof ports],
        description,
        emoji: getFirstEmoji(description),
      };
    })
    .sort((a, b) => {
      if (a.port < b.port) return -1;
      return 1;
    });

  const appOperationsWithOnline: AppOperation[] = await Promise.all(
    appOperations.map(async (appOperation) => {
      const isOnline = await isPortUsed(appOperation.port);
      const newAppOperation: AppOperation = {
        ...appOperation,
        isOnline,
      };
      return newAppOperation;
    })
  );

  return appOperationsWithOnline;
}
