async (
  devices: Device[],
  config?: {
    onlyWithDevices?: boolean;
    onlyWithPapi?: boolean;
    onlyOnline?: boolean;
    withAppsCalculated?: boolean;
  }
) => {
  const { onlyOnline, onlyWithDevices, onlyWithPapi, withAppsCalculated } =
    destructureOptionalObject(config);
  return (
    await Promise.all(
      (
        await db.get("Person")
      ).map(async (x) => {
        const person: Person = {
          ...x,
          devicesCalculated: await Promise.all(
            devices
              .filter((x) => x.personIds?.includes(x.id))
              .filter((x) => (onlyWithPapi ? x.hasPapi : true))
              .map(augmentDevice)
              .sort(sortDevices)
              .filter((x) => (onlyOnline ? x.isOnlineCalculated : true))
              .map((x) => (withAppsCalculated ? deviceGetAppsCalculated(x) : x))
          ),
        };

        return person;
      })
    )
  ).filter((x) => {
    if (!onlyWithDevices) return true;
    return x.devicesCalculated && x.devicesCalculated.length > 0;
  });
}
