async (): Promise<{
  success: boolean;
  peerPeople: Person[];
}> => {
  const devices = await db.get("Device");

  // person array met voor elke person alle devices die een papi hebben
  const peerPeople = await getAugmentedPersons(devices, {
    onlyOnline: false,
    onlyWithDevices: true,
    onlyWithPapi: true,
    withAppsCalculated: true,
  });

  return { success: true, peerPeople };
}
