async (
  slug: string,
  updatedValues: {
    name?: string;
    description?: string;
    authToken?: string;
    isFavorite?: boolean;
    isMe?: boolean;
  }
): Promise<{ isSuccesful: boolean; message: string }> => {
  const updateResult = await db.update(
    "Person",
    (person) => {
      return person.slug === slug;
    },
    (peer) => {
      return { ...peer, ...omitUndefinedValues(updatedValues) };
    }
  );

  return {
    isSuccesful: !!updateResult.isSuccesful,
    message: updateResult.isSuccesful
      ? "Peer updated"
      : `Something went wrong: ${updateResult.message}`,
  };
}
