async (): Promise<ListAppsResult> => {
  return pm2ConnectDisconnect((resolve) => {
    pm2.list((err, processes) => {
      if (err) {
        resolve({ isSuccessful: false, message: err.message });
      }

      const apps = processes
        .filter((x) => x.name?.startsWith(appPrefix))
        .map((x) => ({
          operationName: x.name!.substring(appPrefix.length),
          cpuPercentage: x.monit?.cpu,
          memoryBytes: x.monit?.memory,
        }));

      resolve({ isSuccessful: true, apps });
    });
  });
}
