value:
async <
  TResult extends {
    isSuccessful: boolean;
    error?: Error;
    proc?: pm2.Proc;
    [key: string]: any;
  }
>(
  action: (resolve: (value: TResult) => void) => void
) => {
  const result = await new Promise<{
    isSuccessful: boolean;
    error?: Error;
    proc?: pm2.Proc;
  }>(async (resolve) => {
    const { error } = await pm2Connect();
    if (error) resolve({ isSuccessful: false, error });
    action((value: TResult) => {
      resolve(value);
      pm2.disconnect();
    });
  });

  return result;
}
