async (
  ignore: string[] = []
): Promise<void> => {
  const apps = await listApps();

  console.dir({ appsLength: apps.apps?.length, apps: apps }, { depth: 200 });
  if (!apps.isSuccessful || !apps.apps) {
    log(`something is wrong with listApps`, { type: "error" });
    return;
  }

  const appsToQuit = apps.apps
    .filter((app) => {
      if (!app.operationName) return;

      return !ignore.includes(app.operationName);
    })
    .map((x) => x.operationName);

  await stopApps(appsToQuit);

  log(`Stopped ${appsToQuit.length} apps`, { type: "success" });
}
