value:
(props: {
  items: ContextualPrompt[] | undefined;
  contextContent: string;
  context_projectRelativeFilePath?: string;
}) => {
  const { items, contextContent, context_projectRelativeFilePath } = props;

  const contextSelection = useLastSelection();
  const showPromptAlert = usePromptResultAlert();
  const alert = useAlert();

  const selectItems = items?.map((prompt) => {
    const item: Item<string> = { label: prompt.name, value: prompt.id };

    return item;
  });
  const allSelectItems = [{ label: "Select a prompt", value: "" }].concat(
    selectItems || []
  );
  const [SelectComponent, _, setValue] = useSelect(
    allSelectItems,
    undefined,
    async (selection) => {
      const id = selection?.value;

      const prompt = items?.find((x) => x.id === id);

      if (!prompt) {
        alert?.("Invalid value");
        return;
      }

      setValue(null);

      processPrompt({
        contextualPromptSlug: prompt.slug,
        contextualContent: {
          contextContent,
          context_projectRelativeFilePath,
          contextSelection,
        },
        showPromptAlert,
      });
    }
  );
  return <SelectComponent />;
}
