async (config: {
  contextualPromptSlug?: Slug;
  customPromptContent?: string;
  contextualContent: ContextualContent;
  showPromptAlert: ShowPromptAlertFunction;
  saveNewPromptWithName?: string | null;
}) => {
  const {
    contextualPromptSlug,
    showPromptAlert,
    customPromptContent,
    saveNewPromptWithName,
    contextualContent: {
      contextContent,
      context_projectRelativeFilePath,
      contextSelection,
    },
  } = config;

  const apiResult = await api.processChatGptPrompt({
    contextContent,
    selectionContent: contextSelection || null,
    prompt_projectRelativePath: context_projectRelativeFilePath,
    isDeferred: true,
    // not sure about these?
    contextualPromptSlug,
    customPromptContent,
    saveNewPromptWithName,
    isHeadless: false,
  });

  if (!apiResult.isSuccessful || !apiResult.result?.isSuccessful) {
    showStandardResponse(apiResult);
    return;
  }

  const title = contextualPromptSlug
    ? humanCase(contextualPromptSlug)
    : "Untitled prompt";

  showPromptAlert(
    title,
    apiResult?.result?.result?.text || "No textual result",
    apiResult?.result?.result?.thread,
    context_projectRelativeFilePath
  );
}
