rawText:
 async (props: {
  page: Page;
  spanText: string;
}) => {
  const { page, spanText } = props;
  // getting and clicking on the write something button to post the content
  await page.waitForFunction(
    `document.querySelector("body").innerText.includes("${spanText}")`
  );
  const targetSpanTag = await page.$x(`//span[contains(text(),"${spanText}")]`);
  if (targetSpanTag[0]) {
    //@ts-ignore
    targetSpanTag[0]?.click();
  } else {
    return { success: false, message: `${spanText} tag not found.` };
  }
}
