 async (props: {
  /** the page element from puppeteer got with browser.pages */
  page: Page;
  /** the xpath selector to search for */
  selector: string;
}): Promise<{
  /** was the element found or not */
  found: boolean;
  /** if the element was found, it is returned */
  element?: any;
}> => {
  const { page, selector } = props;
  return new Promise(async (resolve, reject) => {
    // Correct Method
    await page
      .waitForXPath(selector)
      .then((element: any) => {
        resolve({
          found: true,
          element,
        });
      })
      .catch((e: any) => {
        resolve({
          found: false,
        });
      });
  });
}
