 async (props: {
  /** the page element from puppeteer got with browser.pages */
  page: Page;
  /** the css selector to search for */
  selector: string;
  /** (optional) change default timeout to wait for selector */
  timeoutMilliseconds?: number;
}): Promise<boolean> => {
  const { page, selector, timeoutMilliseconds } = props;
  return new Promise(async (resolve, reject) => {
    // Correct Method
    await page
      .waitForSelector(selector, { timeout: timeoutMilliseconds || 30 * 1000 })
      .then(() => {
        resolve(true);
      })
      .catch((e: any) => {
        resolve(false);
      });
  });
}
