 async (
  browser: Browser
): Promise<Page | undefined> => {
  // console.log("Get Idle Page Called");
  const idlePage = (await db.get("BrowserPage")).find((p) => p.isIdle);
  console.log({ idlePage });

  const allPages = await browser.pages();
  const targetPage = allPages.find(
    //@ts-ignore
    (p) => p.target()._targetId === idlePage?.pageId
  );

  return targetPage;
}
