 async (props: GmailLoginPropsType) => {
  // waiting for email input and set email value
  const { page, email, password } = props;
  // setting email address
  await setInputValue({
    page,
    inputValue: email,
    selector: "input[type=email]",
  });

  // clicking on the next button after email input
  await clickOnSpanTag({ page, spanText: "Next" });
  await page.setBypassCSP(true);
  // clicking on the next button after password input

  await page.waitForFunction(
    `document.querySelector("body").innerText.includes("Next")`
  );

  // waiting for the password field to load and set the password
  await setInputValue({
    page,
    inputValue: password,
    selector: "input[type=password]",
  });

  // clicking next button after password entered
  await clickOnSpanTag({ page, spanText: "Next" });
}
