 async (props: OpenMultiTabProps) => {
  const { noOfTabs, tabUrl, browser } = props;
  const allTabs = [];
  for (let i = 0; i < noOfTabs; i++) {
    allTabs.push(browser.newPage());
  }
  const allPages = await Promise.all(allTabs);

  const allPageIds: string[] = await Promise.all(
    //@ts-ignore
    allPages.map((p) => p.target()._targetId)
  );
  //@ts-ignore
  const allPageIdsCreations: Creation<BrowserPage[]> = allPageIds.map(
    (pid) => ({ pageId: pid, isIdle: true })
  );
  await db.upsert("BrowserPage", allPageIdsCreations);
}
